# -*- coding: utf-8 -*-import impimport sysimport optionsimport applicationsimport collector.core as coreimport collector.core.conf as core_settingsimport loggingtry:	imp.find_module('settings')except ImportError:	import sys	sys.stderr.write(		"Error: Can't find the file 'settings2.py' in the directory \		containing %r. It appears you've customized things.")	sys.exit(1)if __name__ == "__main__":	logging.basicConfig(**options.LOG_CONF)	usr_apps = applications.INSTALLED_APPS	core_apps = core_settings.CORE_APPS	workflow = options.WORKFLOW	apps = [core_apps] + usr_apps	sv = core.SysSV(apps, workflow)