# -*- coding: utf-8 -*-import impimport sysimport optionsimport applicationsimport collector as coreimport collector.conf as core_settingsfrom analyzer.lexicon import lexiconfrom analyzer.selection import selectionimport loggingtry:	imp.find_module('settings')except ImportError:	import sys	sys.stderr.write(		"Error: Can't find the file 'settings2.py' in the directory \		containing %r. It appears you've customized things.")	sys.exit(1)if __name__ == "__main__":	logging.basicConfig(**options.LOG_CONF)	cmd = sys.argv[1] if len(sys.argv) > 1 else None	if not cmd or cmd == "collect":		logging.debug("run collector")		usr_apps = applications.INSTALLED_APPS		core_apps = core_settings.CORE_APPS		workflow = options.WORKFLOW		apps = [core_apps] + usr_apps		sv = core.CollectorSupervisor(apps, workflow)	elif cmd == "lexicon":		logging.debug("run lexicon")		lexicon()	elif cmd == "selection":		logging.debug("run selection")		selection()