# -*- coding: utf-8 -*-import impimport sysimport settingsimport collector.core as coreimport collector.core.settings as core_settingstry:	imp.find_module('settings')except ImportError:	import sys	sys.stderr.write(		"Error: Can't find the file 'settings.py' in the directory \		containing %r. It appears you've customized things.")	sys.exit(1)if __name__ == "__main__":	usrapps = settings.USR_APPS	usrconf = settings.USR_CONF	coreapps = core_settings.CORE_APPS	apps = [coreapps] + usrapps	sv = core.SysSV(apps, usrconf)