# -*- coding: utf-8 -*-import impimport sysimport loggingtry:	imp.find_module('settings')except ImportError:	import sys	sys.stderr.write(		"Error: Can't find the file 'settings2.py' in the directory \		containing %r. It appears you've customized things.")	sys.exit(1)if __name__ == "__main__":	from options import LOG_CONF	logging.basicConfig(**LOG_CONF)	cmd = sys.argv[1] if len(sys.argv) > 1 else None	if not cmd or cmd == "collect":		from options import WORKFLOW		from collector.conf import CORE_APPS		from applications import INSTALLED_APPS		from collector import CollectorSupervisor		logging.debug("run collector")		CollectorSupervisor([CORE_APPS] + INSTALLED_APPS, WORKFLOW)	elif cmd == "lexicon":		from analyzer.lexicon import lexicon		from analyzer.selection import selection		logging.debug("run lexicon")		lexicon()	# elif cmd == "selection":    #     logging.debug("run selection")    #     selection()